<?php namespace MyFatoorah\Gateway\Library; use MyFatoorah\Gateway\Library\MyfatoorahApiV2; class  ShippingMyfatoorahApiV2 extends MyfatoorahApiV2{function getShippingCountries(){$url="$this->apiURL/v2/GetCountries";$json=$this->callAPI($url,null,null,'Get Countries');return $json;}function getShippingCities($method,$countryCode,$searchValue=null){$endPoint='GetCities?shippingMethod='.$method.'&countryCode='.$countryCode.'&searchValue='.urlencode(substr($searchValue,0,30));$url="$this->apiURL/v2/".$endPoint;$json=$this->callAPI($url,null,null,'Get Cities - Country : '.$countryCode);return $json;}function calculateShippingCharge($curlData){$url="$this->apiURL/v2/CalculateShippingCharge";$json=$this->callAPI($url,$curlData,null,'Calculate Shipping Charge');return $json;}}